use strict;
use warnings;

require Exporter;
our @ISA = qw(Exporter);
our @EXPORT    = qw(timeLOG LOG WARN ERROR check_path vital_warn);

use Cwd 'abs_path';

use FindBin;
use Carp qw(carp confess croak);

use lib "$FindBin::RealBin";
use lib "$FindBin::RealBin/../";

sub timeLOG
{
	my $timeH = "[" . ftime() . "]";
	print "$timeH @_\n";
}

sub LOG
{
	print "@_\n";
}

sub WARN
{
	carp "WARN: @_\n";
}

sub vital_warn
{
	print STDERR "VITAL WARN: @_\n";
}

sub ERROR
{
	my $ename = shift;
	my $error = $ename;
	confess "FATAL ERROR: $error, [@_]\n";
}

sub check_path
{
	foreach my $file (@_)
	{
		ERROR("the is not defined!") unless $file;
		my @posibilities = (
				$file,
				"$FindBin::RealBin/$file",
				"$FindBin::RealBin/../$file",
				"$FindBin::RealBin/etc/$file",
				"$FindBin::RealBin/../etc/$file",
				"$FindBin::RealBin/../data/$file",
				"$FindBin::RealBin/data/$file",
				"$FindBin::RealBin/example/$file",
				"$FindBin::RealBin/../example/$file"
				);
		my $fullfile;
		
		foreach (@posibilities)
		{
			if (-e $_ && -r $_)
			{
				$fullfile = $_;
				return abs_path($_);
			}
		}
		
	}
	
	ERROR("no_file_exists",@_);
}

sub ftime
{
	my ($sec,$min,$hour,$day,$mon,$year,$wday,$yday,$isdst)=localtime();
	$year += 1900;
	$mon += 1;
	 
	my $ftime = sprintf("%d\-%02d\-%02d %02d:%02d:%02d",$year,$mon,$day,$hour,$min,$sec);
	return $ftime;
}
